import { IAttestTxOptions } from '../Options/Options';
import { TransactionConfig } from 'web3-core';

function createRawTransaction(options: IAttestTxOptions): TransactionConfig{
    const tx = {
        from: options.fromAddress,
        to: options.attestAddress,
        gasPrice: options.gasPrice,
        gasLimit: options.gasLimit,
        nonce: options.nonce,
        data: options.data,
    }
    return tx;
}

export {createRawTransaction};